mapper.select:
 select aaf.FILE_ID,mess,aaf.stute,af.FILE_NAME,af.FILE_URL,af.VOLUME,af.UNIT ,af.FILE_REMARK as fileremark ,af.PRJ_NAME as prjname from ams_archdistribute_file aaf left join (SELECT afr.*,ap.PRJ_NAME from (
SELECT a.* , ar.PRJ_ID from ams_file  a left join ams_receive ar on  a.RC_ID=ar.RC_ID
) afr left join  ams_project ap on ap.PRJ_ID=afr.PRJ_ID) af on af.FILE_ID=aaf.FILE_ID  where DISTRIBUTE_ID = #{distributeId,jdbcType=INTEGER}


   select asu.*,su.name as name2,su.departmentname as departmentname2 from(
select aa.*,su.name,su.departmentname as departmentname from ams_archdistribute aa left join (select su.NAME,su.USER_ID,od.NAME as departmentname from sys_user su left join oa_department od on su.DEPARTMENT_ID=od.DEPARTMENT_ID) su on su.USER_ID=aa.USER_ID where aa.DISTRIBUTE_ID = #{distributeId,jdbcType=INTEGER} 
) asu left join (select su.NAME,su.USER_ID,od.NAME as departmentname from sys_user su left join oa_department od on su.DEPARTMENT_ID=od.DEPARTMENT_ID) su on su.USER_ID=asu.user_id2
